import React from 'react';
import './App.css';



class DetailsPokemon extends React.Component {
    render() {
        const { name, type, image, moreInfo, averageWeight} = this.props.pokemon;

        const itemtype =(item)=>{
            return <li>{item}</li>
        }

        const link = (item,info)=>{
            return <li><a src={item}>  Mais info sobre {info} </a></li>
        }

        const imgPoke =imgPokemon=>{
            return <img src={imgPokemon}/>
        }
        return (
            <div className="Details">
                <span>{name}</span>
                <ul>
                    {itemtype(type)}
                    {link(moreInfo)}
                </ul>
                {imgPoke(image)}
            </div>
        )
    }
}

export default DetailsPokemon;
