import React from 'react';
import './App.css';



class DetailsPokemon extends React.Component {
    render() {
        const { name, type, image, moreInfo} = this.props.pokemon;

        const itemList =item=>{
            return <li></li>
        }

        const link = (item,info)=>{
            return <a src={item}>  Mais info sobre {info} </a>
        }

        const imgPoke =imgPokemon=>{
            return <img src={imgPokemon}/>
        }
        return (
            <div className="Details">
                <span>{name}</span>
                <ul>
                    {itemList(type)}
                    {imgPoke(image)}
                    {itemList(moreInfo)}
                </ul>
            </div>
        )
    }
}

export default DetailsPokemon;
